// Enhanced App.js with conversational handling

import React, { useState, useRef, useEffect } from 'react';
import { Send, BarChart3, TrendingUp, Database, MessageCircle, Loader, Info, Lightbulb, HelpCircle } from 'lucide-react';

// Chart.js components (existing imports remain the same)
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  LineElement,
  PointElement,
} from 'chart.js';
import { Bar, Pie, Doughnut, Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  LineElement,
  PointElement
);

const API_BASE_URL = 'http://localhost:5000/api';

// Enhanced component for displaying different response types
const ResponseMessage = ({ message, type }) => {
  const [showDetails, setShowDetails] = useState(false);

  // Handle greeting responses
  if (type === 'greeting') {
    return (
      <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-6 rounded-lg border border-blue-200">
        <div className="flex items-center mb-4">
          <MessageCircle className="h-6 w-6 text-blue-600 mr-2" />
          <h3 className="text-lg font-semibold text-gray-900">Welcome!</h3>
        </div>
        
        <p className="text-gray-700 mb-4">{message.message}</p>
        
        {/* Capabilities section */}
        <div className="mb-4">
          <h4 className="font-medium text-gray-900 mb-2 flex items-center">
            <TrendingUp className="h-4 w-4 mr-1" />
            What I Can Do:
          </h4>
          <ul className="space-y-1 text-sm text-gray-600">
            {message.capabilities?.map((capability, index) => (
              <li key={index} className="flex items-start">
                <span className="mr-2">•</span>
                {capability}
              </li>
            ))}
          </ul>
        </div>
        
        {/* Sample questions */}
        <div className="mb-4">
          <h4 className="font-medium text-gray-900 mb-2 flex items-center">
            <Lightbulb className="h-4 w-4 mr-1" />
            Try These Questions:
          </h4>
          <div className="space-y-2">
            {message.sample_questions?.map((question, index) => (
              <button
                key={index}
                onClick={() => document.getElementById('question-input').value = question}
                className="block w-full text-left p-2 text-sm bg-white hover:bg-blue-50 rounded border border-gray-200 transition-colors"
              >
                "{question}"
              </button>
            ))}
          </div>
        </div>
        
        {/* Dataset overview */}
        {message.dataset_overview && (
          <div className="mt-4 p-4 bg-white rounded border border-gray-200">
            <h4 className="font-medium text-gray-900 mb-2 flex items-center">
              <Database className="h-4 w-4 mr-1" />
              Your Data Overview:
            </h4>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
              {message.dataset_overview.sales && (
                <div className="text-center">
                  <div className="text-lg font-bold text-blue-600">
                    {message.dataset_overview.sales.total_orders}
                  </div>
                  <div className="text-gray-600">Total Orders</div>
                </div>
              )}
              {message.dataset_overview.customers && (
                <div className="text-center">
                  <div className="text-lg font-bold text-green-600">
                    {message.dataset_overview.customers.total_customers}
                  </div>
                  <div className="text-gray-600">Customers</div>
                </div>
              )}
              {message.dataset_overview.products && (
                <div className="text-center">
                  <div className="text-lg font-bold text-purple-600">
                    {message.dataset_overview.products.total_products}
                  </div>
                  <div className="text-gray-600">Products</div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    );
  }

  // Handle info responses
  if (type === 'info') {
    return (
      <div className="bg-yellow-50 p-6 rounded-lg border border-yellow-200">
        <div className="flex items-center mb-4">
          <Info className="h-6 w-6 text-yellow-600 mr-2" />
          <h3 className="text-lg font-semibold text-gray-900">Here's How I Can Help</h3>
        </div>
        
        <div className="prose prose-sm max-w-none text-gray-700 mb-4">
          {message.message.split('\n').map((paragraph, index) => (
            <p key={index} className="mb-2">{paragraph}</p>
          ))}
        </div>
        
        {message.suggestions && (
          <div className="space-y-2">
            <h4 className="font-medium text-gray-900 mb-2">Get Started:</h4>
            {message.suggestions.map((suggestion, index) => (
              <div key={index} className="p-2 bg-white rounded border border-gray-200 text-sm text-gray-600">
                {suggestion}
              </div>
            ))}
          </div>
        )}
      </div>
    );
  }

  // Handle clarification responses
  if (type === 'clarification') {
    return (
      <div className="bg-orange-50 p-6 rounded-lg border border-orange-200">
        <div className="flex items-center mb-4">
          <HelpCircle className="h-6 w-6 text-orange-600 mr-2" />
          <h3 className="text-lg font-semibold text-gray-900">Let Me Help You</h3>
        </div>
        
        <p className="text-gray-700 mb-4">{message.message}</p>
        
        {message.suggestions && (
          <div className="space-y-2">
            <h4 className="font-medium text-gray-900 mb-2">Try These Instead:</h4>
            {message.suggestions.map((suggestion, index) => (
              <button
                key={index}
                onClick={() => document.getElementById('question-input').value = suggestion}
                className="block w-full text-left p-2 text-sm bg-white hover:bg-orange-50 rounded border border-gray-200 transition-colors"
              >
                {suggestion}
              </button>
            ))}
          </div>
        )}
      </div>
    );
  }

  // Handle data responses (existing chart logic)
  if (type === 'data_response') {
    return (
      <div className="bg-white p-6 rounded-lg border border-gray-200">
        <div className="mb-4">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Analysis Results</h3>
          <p className="text-gray-700">{message.summary}</p>
        </div>
        
        {message.chart_data && (
          <div className="mt-4">
            <ChartRenderer chartData={message.chart_data} />
          </div>
        )}
        
        {message.query_info && (
          <div className="mt-4 p-3 bg-gray-50 rounded text-sm text-gray-600">
            <strong>Query Details:</strong> Found {message.query_info.result_count} results from {message.query_info.collection} collection
          </div>
        )}
      </div>
    );
  }

  // Default message display
  return (
    <div className="bg-gray-50 p-4 rounded-lg">
      <p className="text-gray-700">{message.message || message}</p>
    </div>
  );
};

// Chart renderer component (existing logic enhanced)
const ChartRenderer = ({ chartData }) => {
  const { type, data, mapping } = chartData;
  
  if (!data || data.length === 0) {
    return (
      <div className="text-center py-8 text-gray-500">
        No data available for visualization
      </div>
    );
  }

  // Prepare chart data for Chart.js
  const chartConfig = {
    labels: data.map(item => item[mapping.labels_field] || 'Unknown'),
    datasets: [{
      label: mapping.title || 'Data',
      data: data.map(item => item[mapping.data_field] || 0),
      backgroundColor: [
        'rgba(59, 130, 246, 0.8)',
        'rgba(16, 185, 129, 0.8)',
        'rgba(245, 101, 101, 0.8)',
        'rgba(251, 191, 36, 0.8)',
        'rgba(139, 92, 246, 0.8)',
        'rgba(236, 72, 153, 0.8)',
        'rgba(20, 184, 166, 0.8)',
        'rgba(248, 113, 113, 0.8)',
      ],
      borderColor: [
        'rgba(59, 130, 246, 1)',
        'rgba(16, 185, 129, 1)',
        'rgba(245, 101, 101, 1)',
        'rgba(251, 191, 36, 1)',
        'rgba(139, 92, 246, 1)',
        'rgba(236, 72, 153, 1)',
        'rgba(20, 184, 166, 1)',
        'rgba(248, 113, 113, 1)',
      ],
      borderWidth: 1,
    }],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      title: {
        display: true,
        text: mapping.title || 'Chart',
      },
      legend: {
        display: type === 'pie' || type === 'doughnut',
      },
    },
  };

  const chartStyle = { height: '400px' };

  switch (type) {
    case 'bar':
      return <Bar data={chartConfig} options={chartOptions} style={chartStyle} />;
    case 'pie':
      return <Pie data={chartConfig} options={chartOptions} style={chartStyle} />;
    case 'doughnut':
      return <Doughnut data={chartConfig} options={chartOptions} style={chartStyle} />;
    case 'line':
      return <Line data={chartConfig} options={chartOptions} style={chartStyle} />;
    default:
      return <Bar data={chartConfig} options={chartOptions} style={chartStyle} />;
  }
};

const ConversationalAnalytics = () => {
  const [messages, setMessages] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [systemStatus, setSystemStatus] = useState(null);
  const messagesEndRef = useRef(null);

  // Enhanced suggested queries
  const suggestedQueries = [
    "Hi! What can you do?",
    "What were our top 5 selling products last month?",
    "Show me revenue by region",
    "How has customer engagement trended over the last 6 months?",
    "What's the distribution of customers by country?",
    "Compare sales performance between Q1 and Q2",
    "Tell me about your capabilities"
  ];

  useEffect(() => {
    // Fetch system status on load
    fetch(`${API_BASE_URL}/status`)
      .then(response => response.json())
      .then(data => setSystemStatus(data))
      .catch(error => console.error('Failed to fetch system status:', error));

    // Add initial greeting
    setMessages([{
      type: 'system',
      content: {
        type: 'greeting',
        message: "Hello! 👋 I'm your AI analytics assistant. I can help you explore your business data using natural language.",
        capabilities: [
          "📊 Sales Analysis - Ask about top products, revenue trends, sales performance",
          "👥 Customer Insights - Explore customer demographics, segments, and behavior", 
          "📈 Performance Metrics - Get insights on growth, comparisons, and KPIs",
          "🌍 Regional Analysis - Analyze performance across different regions",
          "📅 Time-based Trends - Understand patterns over time periods"
        ],
        sample_questions: [
          "What were our top 5 selling products last month?",
          "Show me revenue by region",
          "How has customer engagement trended over the last 6 months?",
          "Compare sales performance between Q1 and Q2",
          "What's the distribution of customers by country?"
        ]
      }
    }]);
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!currentQuestion.trim() || isLoading) return;

    const userMessage = {
      type: 'user',
      content: currentQuestion,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setCurrentQuestion('');
    setIsLoading(true);

    try {
      const response = await fetch(`${API_BASE_URL}/query`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: currentQuestion
        }),
      });

      const result = await response.json();

      if (response.ok) {
        const assistantMessage = {
          type: 'assistant',
          content: result,
          timestamp: new Date().toISOString()
        };
        setMessages(prev => [...prev, assistantMessage]);
      } else {
        const errorMessage = {
          type: 'error',
          content: {
            type: 'error',
            message: result.message || result.error || 'An error occurred while processing your request.',
            fallback_message: result.fallback_message
          },
          timestamp: new Date().toISOString()
        };
        setMessages(prev => [...prev, errorMessage]);
      }
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        type: 'error',
        content: {
          type: 'error',
          message: 'Failed to connect to the analytics service. Please try again.',
        },
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSuggestedQuery = (query) => {
    setCurrentQuestion(query);
    document.getElementById('question-input').focus();
  };

  const clearConversation = () => {
    setMessages([{
      type: 'system',
      content: {
        type: 'greeting',
        message: "Hello! 👋 I'm your AI analytics assistant. I can help you explore your business data using natural language.",
        capabilities: [
          "📊 Sales Analysis - Ask about top products, revenue trends, sales performance",
          "👥 Customer Insights - Explore customer demographics, segments, and behavior", 
          "📈 Performance Metrics - Get insights on growth, comparisons, and KPIs",
          "🌍 Regional Analysis - Analyze performance across different regions",
          "📅 Time-based Trends - Understand patterns over time periods"
        ],
        sample_questions: [
          "What were our top 5 selling products last month?",
          "Show me revenue by region",
          "How has customer engagement trended over the last 6 months?",
          "Compare sales performance between Q1 and Q2",
          "What's the distribution of customers by country?"
        ]
      }
    }]);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center">
              <BarChart3 className="h-8 w-8 text-indigo-600 mr-3" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Conversational Analytics</h1>
                <p className="text-sm text-gray-600">Ask questions about your data in natural language</p>
              </div>
            </div>
            
            {/* System Status Indicator */}
            {systemStatus && (
              <div className="flex items-center space-x-4">
                <div className="flex items-center">
                  <div className={`w-2 h-2 rounded-full mr-2 ${
                    systemStatus.services.ai_available ? 'bg-green-500' : 'bg-red-500'
                  }`}></div>
                  <span className="text-sm text-gray-600">AI</span>
                </div>
                <div className="flex items-center">
                  <div className={`w-2 h-2 rounded-full mr-2 ${
                    systemStatus.services.database_available ? 'bg-green-500' : 'bg-red-500'
                  }`}></div>
                  <span className="text-sm text-gray-600">Database</span>
                </div>
                <button
                  onClick={clearConversation}
                  className="text-sm text-gray-500 hover:text-gray-700 px-3 py-1 rounded hover:bg-gray-100"
                >
                  Clear Chat
                </button>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          
          {/* Sidebar with suggestions */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-sm p-6 sticky top-8">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <MessageCircle className="h-5 w-5 mr-2 text-indigo-600" />
                Suggested Questions
              </h3>
              <div className="space-y-2">
                {suggestedQueries.map((query, index) => (
                  <button
                    key={index}
                    onClick={() => handleSuggestedQuery(query)}
                    className="w-full text-left p-3 text-sm bg-gray-50 hover:bg-indigo-50 rounded-lg transition-colors duration-200"
                  >
                    {query}
                  </button>
                ))}
              </div>
            </div>
          </div>

          {/* Main Chat Interface */}
          <div className="lg:col-span-3">
            <div className="bg-white rounded-lg shadow-sm">
              {/* Messages Area */}
              <div className="h-96 overflow-y-auto p-6 space-y-6">
                {messages.map((message, index) => (
                  <div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
                    <div className={`max-w-4xl ${
                      message.type === 'user' 
                        ? 'bg-indigo-600 text-white' 
                        : message.type === 'error'
                        ? 'bg-red-50 text-red-700 border border-red-200'
                        : 'bg-gray-50 text-gray-800'
                    } rounded-lg p-4`}>
                      {message.type === 'user' ? (
                        <p>{message.content}</p>
                      ) : message.type === 'error' ? (
                        <div>
                          <p className="font-medium">Error</p>
                          <p>{message.content.message}</p>
                          {message.content.fallback_message && (
                            <p className="mt-2 text-sm">{message.content.fallback_message}</p>
                          )}
                        </div>
                      ) : (
                        <ResponseMessage message={message.content} type={message.content.type} />
                      )}
                    </div>
                  </div>
                ))}
                
                {isLoading && (
                  <div className="flex justify-start">
                    <div className="bg-gray-50 text-gray-800 rounded-lg p-4">
                      <div className="flex items-center space-x-2">
                        <Loader className="h-4 w-4 animate-spin" />
                        <span>Analyzing your question...</span>
                      </div>
                    </div>
                  </div>
                )}
                
                <div ref={messagesEndRef} />
              </div>

              {/* Input Area */}
              <div className="border-t border-gray-200 p-6">
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div className="flex space-x-4">
                    <input
                      id="question-input"
                      type="text"
                      value={currentQuestion}
                      onChange={(e) => setCurrentQuestion(e.target.value)}
                      placeholder="Ask me anything about your data... (e.g., 'Hi, what can you do?' or 'Show me top products')"
                      className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                      disabled={isLoading}
                    />
                    <button
                      type="submit"
                      disabled={isLoading || !currentQuestion.trim()}
                      className="px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center space-x-2"
                    >
                      {isLoading ? (
                        <Loader className="h-4 w-4 animate-spin" />
                      ) : (
                        <Send className="h-4 w-4" />
                      )}
                      <span>Ask</span>
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ConversationalAnalytics;
