import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  LineElement,
  PointElement,
} from 'chart.js';
import { Bar, Pie, Doughnut, Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  LineElement,
  PointElement
);

const API_BASE_URL = 'http://localhost:5000/api';

const ConversationalAnalytics = () => {
  const [query, setQuery] = useState('');
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('answer');
  const [error, setError] = useState(null);
  
  const inputRef = useRef(null);

  // Sample suggestions for the interface
  const suggestions = [
    { icon: TrendingUp, text: "Analyze", description: "Deep dive into trends and patterns" },
    { icon: BarChart3, text: "Visualize", description: "Create charts and graphs" },
    { icon: Database, text: "Query", description: "Search database insights" },
    { icon: Zap, text: "Quick Stats", description: "Get instant metrics" }
  ];

  // Focus input on load
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  // Chart rendering function
  const renderChart = (chartData) => {
    if (!chartData) return null;

    const chartOptions = {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        legend: {
          position: 'bottom',
          labels: {
            padding: 20,
            usePointStyle: true,
            font: {
              size: 12,
              family: "'Inter', sans-serif"
            }
          }
        },
        tooltip: {
          backgroundColor: 'rgba(0, 0, 0, 0.8)',
          titleColor: '#fff',
          bodyColor: '#fff',
          borderColor: 'rgba(255, 255, 255, 0.1)',
          borderWidth: 1
        }
      },
      scales: chartData.type !== 'pie' && chartData.type !== 'doughnut' ? {
        x: {
          grid: {
            display: false
          },
          ticks: {
            font: {
              family: "'Inter', sans-serif"
            }
          }
        },
        y: {
          grid: {
            color: 'rgba(0, 0, 0, 0.05)'
          },
          ticks: {
            font: {
              family: "'Inter', sans-serif"
            }
          }
        }
      } : {}
    };

    switch (chartData.type) {
      case 'bar':
        return <Bar data={chartData.data} options={chartOptions} />;
      case 'pie':
        return <Pie data={chartData.data} options={chartOptions} />;
      case 'doughnut':
        return <Doughnut data={chartData.data} options={chartOptions} />;
      case 'line':
        return <Line data={chartData.data} options={chartOptions} />;
      default:
        return <Bar data={chartData.data} options={chartOptions} />;
    }
  };

  // Handle query submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!query.trim() || isLoading) return;

    setIsLoading(true);
    setError(null);
    setResult(null);

    try {
      const response = await fetch(`${API_BASE_URL}/query`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query: query.trim() }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setResult(data);
      setActiveTab('answer');
    } catch (error) {
      console.error('Error submitting query:', error);
      setError('Failed to process your query. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  // Handle suggestion click
  const handleSuggestionClick = (suggestion) => {
    setQuery(`${suggestion.text.toLowerCase()} `);
    if (inputRef.current) {
      inputRef.current.focus();
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      <div className="max-w-4xl mx-auto px-4 py-8">
        
        {/* Header Logo */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-slate-800 mb-2">
            <span className="bg-gradient-to-r from-blue-600 to-cyan-600 bg-clip-text text-transparent">
              perplexity
            </span>
          </h1>
          <p className="text-slate-600 text-sm">
            Conversational Analytics Platform
          </p>
        </div>

        {/* Search Interface */}
        <div className="mb-8">
          <form onSubmit={handleSubmit} className="relative">
            <div className="relative bg-white rounded-xl shadow-lg border border-slate-200 hover:shadow-xl transition-all duration-300">
              <div className="flex items-center p-4">
                <Search className="w-5 h-5 text-slate-400 mr-3" />
                <input
                  ref={inputRef}
                  type="text"
                  value={query}
                  onChange={(e) => setQuery(e.target.value)}
                  placeholder="Ask anything or @mention a Space"
                  className="flex-1 text-base text-slate-700 placeholder-slate-400 bg-transparent border-none outline-none"
                  disabled={isLoading}
                />
                <button
                  type="submit"
                  disabled={!query.trim() || isLoading}
                  className="ml-3 p-2 bg-blue-600 hover:bg-blue-700 disabled:bg-slate-300 text-white rounded-lg transition-colors duration-200 disabled:cursor-not-allowed"
                >
                  {isLoading ? (
                    <Loader2 className="w-4 h-4 animate-spin" />
                  ) : (
                    <Send className="w-4 h-4" />
                  )}
                </button>
              </div>
            </div>
          </form>

          {/* Suggestion Buttons */}
          {!result && !isLoading && (
            <div className="flex flex-wrap gap-3 mt-6 justify-center">
              {suggestions.map((suggestion, index) => (
                <button
                  key={index}
                  onClick={() => handleSuggestionClick(suggestion)}
                  className="flex items-center gap-2 px-4 py-2 bg-white hover:bg-blue-50 text-slate-700 hover:text-blue-700 rounded-lg border border-slate-200 hover:border-blue-200 transition-all duration-200 shadow-sm hover:shadow-md group"
                >
                  <suggestion.icon className="w-4 h-4 text-slate-500 group-hover:text-blue-600" />
                  <span className="font-medium text-sm">{suggestion.text}</span>
                </button>
              ))}
            </div>
          )}
        </div>

        {/* Loading State */}
        {isLoading && (
          <div className="text-center py-12">
            <div className="inline-flex items-center gap-3 px-6 py-3 bg-white rounded-lg shadow-sm border border-slate-200">
              <Loader2 className="w-5 h-5 animate-spin text-blue-600" />
              <span className="text-slate-700 font-medium">Analyzing your data...</span>
            </div>
          </div>
        )}

        {/* Error State */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
            <p className="text-red-800 text-sm">{error}</p>
          </div>
        )}

        {/* Results */}
        {result && !isLoading && (
          <div className="space-y-6">
            
            {/* Tab Navigation */}
            <div className="flex items-center gap-1 bg-white rounded-lg p-1 shadow-sm border border-slate-200">
              {[
                { id: 'answer', label: 'Answer', icon: FileText },
                { id: 'charts', label: 'Charts', icon: BarChart3 },
                { id: 'sources', label: 'Sources', icon: ExternalLink },
                { id: 'steps', label: 'Steps', icon: ChevronDown }
              ].map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center gap-2 px-4 py-2 rounded-md text-sm font-medium transition-all duration-200 ${
                    activeTab === tab.id
                      ? 'bg-blue-100 text-blue-700'
                      : 'text-slate-600 hover:text-slate-800 hover:bg-slate-50'
                  }`}
                >
                  <tab.icon className="w-4 h-4" />
                  {tab.label}
                </button>
              ))}
            </div>

            {/* Answer Tab */}
            {activeTab === 'answer' && (
              <div className="bg-white rounded-xl shadow-sm border border-slate-200 p-6">
                <h2 className="text-xl font-semibold text-slate-800 mb-4">
                  Analysis Results
                </h2>
                <div className="prose prose-slate max-w-none">
                  <p className="text-slate-700 leading-relaxed">
                    {result.summary || result.text_summary || 'Here are your analysis results.'}
                  </p>
                </div>
              </div>
            )}

            {/* Charts Tab */}
            {activeTab === 'charts' && result.chart_data && (
              <div className="bg-white rounded-xl shadow-sm border border-slate-200 p-6">
                <h2 className="text-xl font-semibold text-slate-800 mb-6">
                  Data Visualization
                </h2>
                <div className="h-96">
                  {renderChart(result.chart_data)}
                </div>
              </div>
            )}

            {/* Sources Tab */}
            {activeTab === 'sources' && (
              <div className="bg-white rounded-xl shadow-sm border border-slate-200 p-6">
                <h2 className="text-xl font-semibold text-slate-800 mb-4">
                  Data Sources
                </h2>
                <div className="space-y-3">
                  <div className="flex items-center gap-3 p-3 bg-slate-50 rounded-lg">
                    <Database className="w-5 h-5 text-slate-500" />
                    <div>
                      <p className="font-medium text-slate-800">MongoDB Database</p>
                      <p className="text-sm text-slate-600">Analytics collection</p>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Steps Tab */}
            {activeTab === 'steps' && (
              <div className="bg-white rounded-xl shadow-sm border border-slate-200 p-6">
                <h2 className="text-xl font-semibold text-slate-800 mb-4">
                  Processing Steps
                </h2>
                <div className="space-y-3">
                  <div className="flex items-start gap-3">
                    <div className="flex-shrink-0 w-6 h-6 bg-blue-100 text-blue-700 rounded-full flex items-center justify-center text-sm font-medium">1</div>
                    <div>
                      <p className="font-medium text-slate-800">Query Analysis</p>
                      <p className="text-sm text-slate-600">Processed natural language query using AI</p>
                    </div>
                  </div>
                  <div className="flex items-start gap-3">
                    <div className="flex-shrink-0 w-6 h-6 bg-blue-100 text-blue-700 rounded-full flex items-center justify-center text-sm font-medium">2</div>
                    <div>
                      <p className="font-medium text-slate-800">Database Query</p>
                      <p className="text-sm text-slate-600">Generated and executed MongoDB aggregation</p>
                    </div>
                  </div>
                  <div className="flex items-start gap-3">
                    <div className="flex-shrink-0 w-6 h-6 bg-blue-100 text-blue-700 rounded-full flex items-center justify-center text-sm font-medium">3</div>
                    <div>
                      <p className="font-medium text-slate-800">Visualization</p>
                      <p className="text-sm text-slate-600">Created appropriate chart representation</p>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Follow-up Section */}
            <div className="bg-slate-50 rounded-xl p-6 border border-slate-200">
              <h3 className="text-lg font-semibold text-slate-800 mb-4">Related</h3>
              <div className="space-y-2">
                <button className="w-full text-left p-3 text-sm bg-white hover:bg-blue-50 hover:text-blue-700 rounded-lg transition-all duration-200 border border-transparent hover:border-blue-200">
                  Can I see a visual representation of this data?
                </button>
                <button className="w-full text-left p-3 text-sm bg-white hover:bg-blue-50 hover:text-blue-700 rounded-lg transition-all duration-200 border border-transparent hover:border-blue-200">
                  How does this compare to last quarter?
                </button>
                <button className="w-full text-left p-3 text-sm bg-white hover:bg-blue-50 hover:text-blue-700 rounded-lg transition-all duration-200 border border-transparent hover:border-blue-200">
                  What trends can you identify in this data?
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ConversationalAnalytics;
