// frontend/src/App.js - Enhanced with conversational support

import React, { useState, useEffect, useRef } from 'react';
import { 
  BarChart3, 
  MessageCircle, 
  Send, 
  Loader, 
  TrendingUp, 
  Users, 
  ShoppingCart,
  DollarSign,
  Bot,
  User,
  Lightbulb,
  Database
} from 'lucide-react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from 'chart.js';
import { Bar, Pie, Doughnut, Line } from 'react-chartjs-2';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

function App() {
  const [messages, setMessages] = useState([]);
  const [currentQuery, setCurrentQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [stats, setStats] = useState(null);
  const [suggestions, setSuggestions] = useState([]);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    // Load initial stats and suggestions
    fetchStats();
    fetchSuggestions();
    
    // Add welcome message
    const welcomeMessage = {
      type: 'bot',
      content: "Hello! I'm your conversational analytics assistant. I can help you explore your business data using natural language. What would you like to know?",
      timestamp: new Date(),
      conversational: true
    };
    setMessages([welcomeMessage]);
  }, []);

  const fetchStats = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/stats');
      if (response.ok) {
        const data = await response.json();
        setStats(data.overview);
      }
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const fetchSuggestions = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/suggestions');
      if (response.ok) {
        const data = await response.json();
        setSuggestions(data.suggestions || []);
      }
    } catch (error) {
      console.error('Error fetching suggestions:', error);
      // Fallback suggestions
      setSuggestions([
        "What are my top selling products?",
        "Show me sales by region",
        "Customer distribution analysis",
        "Revenue trends over time"
      ]);
    }
  };

  const renderChart = (chartData) => {
    if (!chartData) return null;
    
    const { type, data, options } = chartData;
    
    const commonOptions = {
      ...options,
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        ...options.plugins,
        legend: {
          ...options.plugins?.legend,
          labels: {
            usePointStyle: true,
            padding: 20,
            font: {
              size: 12
            }
          }
        }
      }
    };

    switch (type) {
      case 'bar':
        return <Bar data={data} options={commonOptions} />;
      case 'pie':
        return <Pie data={data} options={commonOptions} />;
      case 'doughnut':
        return <Doughnut data={data} options={commonOptions} />;
      case 'line':
        return <Line data={data} options={commonOptions} />;
      default:
        return <Bar data={data} options={commonOptions} />;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!currentQuery.trim() || isLoading) return;

    const userMessage = {
      type: 'user',
      content: currentQuery,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/query', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: currentQuery
        })
      });

      const data = await response.json();

      if (response.ok) {
        const botMessage = {
          type: 'bot',
          content: data.summary,
          chartData: data.chart_data,
          timestamp: new Date(),
          conversational: data.conversational || false,
          suggestions: data.suggestions,
          aiPowered: data.ai_powered
        };

        setMessages(prev => [...prev, botMessage]);
        
        // Update suggestions if provided
        if (data.suggestions && data.suggestions.length > 0) {
          setSuggestions(data.suggestions);
        }
      } else {
        const errorMessage = {
          type: 'error',
          content: data.error || 'An error occurred while processing your request.',
          timestamp: new Date()
        };
        setMessages(prev => [...prev, errorMessage]);
      }
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        type: 'error',
        content: `Connection error: ${error.message}. Make sure the backend server is running on http://localhost:5000`,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
      setCurrentQuery('');
    }
  };

  const handleSuggestedQuery = (query) => {
    setCurrentQuery(query);
  };

  const getMessageIcon = (messageType, isConversational) => {
    if (messageType === 'user') {
      return <User className="h-4 w-4" />;
    } else if (isConversational) {
      return <MessageCircle className="h-4 w-4" />;
    } else {
      return <BarChart3 className="h-4 w-4" />;
    }
  };

  const getMessageStyle = (messageType, isConversational) => {
    if (messageType === 'user') {
      return 'bg-indigo-600 text-white';
    } else if (messageType === 'error') {
      return 'bg-red-50 text-red-800 border border-red-200';
    } else if (isConversational) {
      return 'bg-blue-50 text-blue-900 border border-blue-200';
    } else {
      return 'bg-gray-50 text-gray-900 border border-gray-200';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-3">
              <div className="bg-indigo-600 p-2 rounded-lg">
                <BarChart3 className="h-6 w-6 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Conversational Analytics</h1>
                <p className="text-sm text-gray-500">Ask questions about your data in natural language</p>
              </div>
            </div>
            
            {/* Stats Display */}
            {stats && (
              <div className="hidden md:flex space-x-6">
                <div className="text-center">
                  <div className="flex items-center justify-center space-x-1">
                    <ShoppingCart className="h-4 w-4 text-indigo-600" />
                    <div className="text-2xl font-bold text-indigo-600">{stats.total_sales?.toLocaleString() || 0}</div>
                  </div>
                  <div className="text-xs text-gray-500">Total Orders</div>
                </div>
                <div className="text-center">
                  <div className="flex items-center justify-center space-x-1">
                    <DollarSign className="h-4 w-4 text-green-600" />
                    <div className="text-2xl font-bold text-green-600">${stats.total_revenue?.toLocaleString() || 0}</div>
                  </div>
                  <div className="text-xs text-gray-500">Total Revenue</div>
                </div>
                <div className="text-center">
                  <div className="flex items-center justify-center space-x-1">
                    <Users className="h-4 w-4 text-purple-600" />
                    <div className="text-2xl font-bold text-purple-600">{stats.total_customers?.toLocaleString() || 0}</div>
                  </div>
                  <div className="text-xs text-gray-500">Customers</div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Suggested Queries Sidebar */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <Lightbulb className="h-5 w-5 mr-2 text-yellow-500" />
                Suggested Questions
              </h3>
              <div className="space-y-2">
                {suggestions.map((query, index) => (
                  <button
                    key={index}
                    onClick={() => handleSuggestedQuery(query)}
                    className="w-full text-left p-3 text-sm bg-gray-50 hover:bg-indigo-50 rounded-lg transition-colors duration-200 border border-gray-200 hover:border-indigo-200"
                  >
                    <div className="flex items-start space-x-2">
                      <MessageCircle className="h-4 w-4 text-gray-400 mt-0.5 flex-shrink-0" />
                      <span className="text-gray-700">{query}</span>
                    </div>
                  </button>
                ))}
              </div>
              
              {/* Quick Actions */}
              <div className="mt-6 pt-4 border-t border-gray-200">
                <h4 className="text-sm font-medium text-gray-900 mb-3">Quick Actions</h4>
                <div className="space-y-2">
                  <button
                    onClick={() => handleSuggestedQuery("Hi, what can you do?")}
                    className="w-full text-left p-2 text-sm text-blue-600 hover:bg-blue-50 rounded-lg transition-colors duration-200"
                  >
                    <div className="flex items-center space-x-2">
                      <Bot className="h-4 w-4" />
                      <span>About this system</span>
                    </div>
                  </button>
                  <button
                    onClick={() => handleSuggestedQuery("What data do you have?")}
                    className="w-full text-left p-2 text-sm text-green-600 hover:bg-green-50 rounded-lg transition-colors duration-200"
                  >
                    <div className="flex items-center space-x-2">
                      <Database className="h-4 w-4" />
                      <span>Available data</span>
                    </div>
                  </button>
                  <button
                    onClick={() => handleSuggestedQuery("Show me some examples")}
                    className="w-full text-left p-2 text-sm text-purple-600 hover:bg-purple-50 rounded-lg transition-colors duration-200"
                  >
                    <div className="flex items-center space-x-2">
                      <TrendingUp className="h-4 w-4" />
                      <span>Example queries</span>
                    </div>
                  </button>
                </div>
              </div>
            </div>
          </div>

          {/* Main Chat Interface */}
          <div className="lg:col-span-3">
            <div className="bg-white rounded-lg shadow-sm">
              {/* Messages Area */}
              <div className="h-96 overflow-y-auto p-6 space-y-6">
                {messages.map((message, index) => (
                  <div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
                    <div className={`max-w-2xl ${getMessageStyle(message.type, message.conversational)} rounded-lg p-4`}>
                      {/* Message Header */}
                      <div className="flex items-center space-x-2 mb-2">
                        {getMessageIcon(message.type, message.conversational)}
                        <span className="text-xs font-medium opacity-70">
                          {message.type === 'user' ? 'You' : 'Analytics Assistant'}
                          {message.aiPowered && <span className="ml-1 text-xs bg-green-100 text-green-800 px-1 rounded">AI</span>}
                        </span>
                      </div>
                      
                      {/* Message Content */}
                      <div className="text-sm whitespace-pre-wrap">
                        {message.content}
                      </div>
                      
                      {/* Chart Display */}
                      {message.chartData && (
                        <div className="mt-4 bg-white p-4 rounded-lg border border-gray-200">
                          <div className="h-64">
                            {renderChart(message.chartData)}
                          </div>
                        </div>
                      )}
                      
                      {/* Dynamic Suggestions */}
                      {message.suggestions && message.suggestions.length > 0 && (
                        <div className="mt-3 pt-3 border-t border-gray-200">
                          <div className="text-xs text-gray-500 mb-2">Try asking:</div>
                          <div className="flex flex-wrap gap-2">
                            {message.suggestions.slice(0, 3).map((suggestion, idx) => (
                              <button
                                key={idx}
                                onClick={() => handleSuggestedQuery(suggestion)}
                                className="text-xs bg-gray-100 hover:bg-gray-200 px-2 py-1 rounded transition-colors"
                              >
                                {suggestion}
                              </button>
                            ))}
                          </div>
                        </div>
                      )}
                      
                      <div className="text-xs opacity-70 mt-2">
                        {message.timestamp.toLocaleTimeString()}
                      </div>
                    </div>
                  </div>
                ))}
                
                {/* Loading Message */}
                {isLoading && (
                  <div className="flex justify-start">
                    <div className="bg-gray-100 text-gray-900 rounded-lg p-4 flex items-center space-x-2">
                      <Loader className="h-4 w-4 animate-spin" />
                      <span className="text-sm">Analyzing your data...</span>
                    </div>
                  </div>
                )}
                
                <div ref={messagesEndRef} />
              </div>

              {/* Input Area */}
              <div className="border-t p-6">
                <form onSubmit={handleSubmit} className="flex space-x-4">
                  <input
                    type="text"
                    value={currentQuery}
                    onChange={(e) => setCurrentQuery(e.target.value)}
                    placeholder="Ask a question about your data or say hello..."
                    className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    disabled={isLoading}
                  />
                  <button
                    type="submit"
                    disabled={isLoading || !currentQuery.trim()}
                    className="px-6 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center space-x-2"
                  >
                    {isLoading ? (
                      <Loader className="h-4 w-4 animate-spin" />
                    ) : (
                      <Send className="h-4 w-4" />
                    )}
                    <span>{isLoading ? 'Processing...' : 'Send'}</span>
                  </button>
                </form>
                
                {/* Quick Start Examples */}
                <div className="mt-3 flex flex-wrap gap-2">
                  <button
                    onClick={() => handleSuggestedQuery("Hello")}
                    className="text-xs bg-blue-100 hover:bg-blue-200 text-blue-800 px-2 py-1 rounded transition-colors"
                  >
                     Say Hello
                  </button>
                  <button
                    onClick={() => handleSuggestedQuery("What do you do?")}
                    className="text-xs bg-green-100 hover:bg-green-200 text-green-800 px-2 py-1 rounded transition-colors"
                  >
                     Learn More
                  </button>
                  <button
                    onClick={() => handleSuggestedQuery("Help me get started")}
                    className="text-xs bg-purple-100 hover:bg-purple-200 text-purple-800 px-2 py-1 rounded transition-colors"
                  >
                     Get Started
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
